mapper.update:
UPDATE cmt_comment
SET	contents = #{contents}
,modifyDate = now()
WHERE commentId = #{commentId}

UPDATE  cmt_comment
SET likeCnt = #{likeCnt}
WHERE commentId = #{commentId}

mapper.select:
SELECT 	count(1)
FROM cmt_like
WHERE commentId = #{commentId}
AND userId = #{userId}

mapper.delete:
DELETE FROM cmt_like
WHERE commentId = #{commentId}
AND userId = #{userId}

mapper.update:
UPDATE  cmt_comment
SET reportCnt = #{reportCnt}
WHERE commentId = #{commentId}

mapper.select:
SELECT 	count(1)
FROM cmt_report
WHERE userId = #{userId}

	AND commentId = #{commentId}


	AND replyId = #{replyId}
