mapper.update:
UPDATE cmt_contents SET 
	contentsNm = #{contentsNm}
	,communityId = #{communityId}
	,contents = #{contents}
	,modifyId = #{modifyId}
	,modifyDate = now()
WHERE contentsId = #{contentsId}

mapper.select:
SELECT count(1)
FROM cmt_contents
WHERE 1=1

	and contentsId = #{contentsId}


	and communityId = #{communityId}
